import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { fetchAPI } from "./api";

const BookingPage = ({ dispatch, handleSubmit, submitting }) => {
  const [date, setDate] = useState("");
  const [time, setTime] = useState("17:00");
  const [guests, setGuests] = useState(1);
  const [occasion, setOccasion] = useState("Birthday");

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    if (submitting) {
      return; // Prevent multiple form submissions while submitting
    }

    try {
      // Perform submission logic or API call
      console.log("Form submitted:", { date, time, guests, occasion });

      // Update available times based on the selected date
      const newAvailableTimes = await fetchAPI(date);
      dispatch({ type: "UPDATE_TIMES", availableTimes: newAvailableTimes });

      // Call the handleSubmit function passed as prop
      await handleSubmit({ date, time, guests, occasion });
    } catch (error) {
      console.log("Error:", error);
    }
  };

  return (
    <form
      style={{ display: "grid", maxWidth: "200px", gap: "20px" }}
      onSubmit={handleFormSubmit}
    >
      <label htmlFor="res-date">Choose date</label>
      <input
        type="date"
        id="res-date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />

      <label htmlFor="res-time">Choose time</label>
      <select
        id="res-time"
        value={time}
        onChange={(e) => setTime(e.target.value)}
