import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const BookingPage = ({
  availableTimes,
  setAvailableTimes,
  submitForm,
  dispatch,
}) => {
  const [date, setDate] = useState("");
  const [time, setTime] = useState("17:00");
  const [guests, setGuests] = useState(1);
  const [occasion, setOccasion] = useState("Birthday");
  const [submitting, setSubmitting] = useState(false); // Added submitting state

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (submitting) {
      return; // Prevent multiple form submissions while submitting
    }

    setSubmitting(true); // Start submitting

    try {
      // Perform submission logic or API call
      console.log("Form submitted:", { date, time, guests, occasion });

      // Update available times based on the selected date
      dispatch({ type: "UPDATE_TIMES", date }); // Dispatch action to update available times

      const response = await submitForm({ date, time, guests, occasion }); // Await the submitForm function

      if (response === true) {
        navigate("/booking-confirmed"); // Navigate to booking confirmation page
      }
    } catch (error) {
      // Handle API call or submission error
      console.log("Error:", error);
    } finally {
      setSubmitting(false); // Reset submitting state
    }
  };

  return (
    <form
      style={{ display: "grid", maxWidth: "200px", gap: "20px" }}
      onSubmit={handleSubmit}
    >
      <label htmlFor="res-date">Choose date</label>
      <input
        type="date"
        id="res-date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />

      <label htmlFor="res-time">Choose time</label>
      <select
        id="res-time"
        value={time}
        onChange={(e) => setTime(e.target.value)}
      >
        {availableTimes.map((timeOption) => (
          <option key={timeOption} value={timeOption}>
            {timeOption}
          </option>
        ))}
      </select>

      <label htmlFor="guests">Number of guests</label>
      <input
        type="number"
        placeholder="1"
        min="1"
        max="10"
        id="guests"
        value={guests}
        onChange={(e) => setGuests(e.target.value)}
      />

      <label htmlFor="occasion">Occasion</label>
      <select
        id="occasion"
        value={occasion}
        onChange={(e) => setOccasion(e.target.value)}
      >
        <option value="Birthday">Birthday</option>
        <option value="Anniversary">Anniversary</option>
      </select>

      <input
        type="submit"
        value="Make Your reservation"
        disabled={submitting} // Disable the button while submitting
      />
    </form>
  );
};

export default BookingPage;
