import React, { useReducer } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import About from "./About";
import CustomersSay from "./CustomersSay";
import BookingPage from "./BookingPage";
import CallToAction from "./CallToAction";
import Homepage from "./HomePage";
import Specials from "./Specials";
import ConfirmedBooking from "./ConfirmedBooking";
import { useNavigate } from "react-router-dom";

const initializeTimes = () => {
  return ["17:00", "18:00", "19:00", "20:00", "21:00", "22:00"];
};

const updateTimes = (state, date) => {
  // Logic to update available times based on the selected date
  // For now, returning the same available times regardless of the date
  return state;
};

const reducer = (state, action) => {
  switch (action.type) {
    case "UPDATE_TIMES":
      return updateTimes(state, action.date);
    default:
      return state;
  }
};

const Main = () => {
  const [availableTimes, dispatch] = useReducer(reducer, [], initializeTimes);

  const navigate = useNavigate();

  const submitAPI = (formData) => {
    // Perform API call or submission logic
    return new Promise((resolve, reject) => {
      // Simulating API call with a timeout
      setTimeout(() => {
        // Simulating successful API response
        resolve(true);
        // Simulating API error response
        // reject("API Error");
      }, 1000);
    });
  };

  const submitForm = (formData) => {
    // Perform API call or submission logic
    submitAPI(formData)
      .then((response) => {
        if (response === true) {
          navigate("/booking-confirmed"); // Navigate to booking confirmation page
        }
      })
      .catch((error) => {
        // Handle API call or submission error
        console.log("Error:", error);
      });
  };

  return (
    <Routes>
      <Route path="/" element={<Homepage />} />
      <Route path="/specials" element={<Specials />} />
      <Route path="/about" element={<About />} />
      <Route path="/testimonials" element={<CustomersSay />} />
      <Route path="/booking-confirmed" element={<ConfirmedBooking />} />
      <Route
        path="/contact"
        element={
          <BookingPage
            availableTimes={availableTimes}
            dispatch={dispatch}
            submitForm={submitForm}
          />
        }
      />
      <Route path="/order-online" element={<CallToAction />} />
      <Route
        path="/booking-page"
        element={
          <BookingPage
            availableTimes={availableTimes}
            dispatch={dispatch}
            submitForm={submitForm}
          />
        }
      />
    </Routes>
  );
};

export default Main;
